import { useDebugValue, useEffect, useState } from "react";
import Menu from "../menu";

const GroupSetting = ({ data, setData, settingprofile, setsettingprofile }) => {

    let grouplist = data.map((item)=>   item.group    );
    const [selectedgroup, setselectedgroup] = useState("");
    const ChangeGroup = (e) => {
        setselectedgroup(e.target.value);
    }

    const [color, setcolor] = useState("");
    const Changecolor = (e) => {
        setcolor(e.target.value);
    }

    let profilegroup = settingprofile["group"].map((i)=>i.name);

    useEffect(()=>{
        if (profilegroup.includes(selectedgroup)) {
            setcolor(profilegroup.group[profilegroup.indexOf(selectedgroup)].color);
        }
    },[])
    

    return (
        <div className="app">
            <Menu/>
            <div className="groupsetting">
                <h1>Group Setting</h1>
                <h3 id="groupsetting">Color</h3>
                <div className="changecolor">
                    <input list="grouplist" id="changegroup" value={selectedgroup} onChange={ChangeGroup} />
                    <datalist id="grouplist">
                    {grouplist.map((d)=> {
                        return (
                            <option value={d} />
                        )
                    })}
                    </datalist>
                    <input type="color" id="inputcolor" value={color} onChange={Changecolor} />
                </div>
                {JSON.stringify(profilegroup)}
            </div>
        </div>
    )
};

export default GroupSetting;
