import Popup from "reactjs-popup";
import { HiOutlineMenu } from "react-icons/hi";
import { NavLink } from "react-router-dom";
import { useEffect } from "react";
import ByDate from "./bydate";

import { Swiper, SwiperSlide } from "swiper/react"
import 'swiper/css';
import 'swiper/css/pagination';
import 'swiper/css/navigation';
import { Pagination, Navigation } from 'swiper/modules';

const Cal = ({ data, setData, rerenderStatus }) => {

    useEffect(()=> {
        if (localStorage.getItem("user") != null) {
            setData(JSON.parse(localStorage.getItem("user")))
        }
    }, ["a"]);

    useEffect(()=> {
        if (rerenderStatus.current == false) {
            return;
        } else {
            localStorage.setItem('user', JSON.stringify(data));
            rerenderStatus.current = false; 
        }
    }, [data]);

    return (
    <div>
        <div className="app">
            <div className="menu"><Popup trigger={<button className="menu"><HiOutlineMenu/></button>} modal nested>
                {
                    close => (
                        <div className='modal'>
                            <h3 className='content'>選擇頁面</h3>
                            <div className="navlink">
                                <NavLink to="/" style={{ textDecoration: 'none' }} ><li id="selectPage">主頁面</li></NavLink>
                                <NavLink to="/Calendar" style={{ textDecoration: 'none' }} ><li id="selectPage">日曆</li></NavLink>
                            </div>
                            <div>
                                <button id="closePopup" onClick=
                                    {() => close()}>
                                        Close
                                </button>
                            </div>
                        </div>
                    )
                }
                </Popup>
            </div>
            <div className="cal">
                <h1>CALENDAR</h1>
                <div id="changecalendarviewtype">
                <Swiper slidesPerView={1}
                spaceBetween={30}
                loop={true}
                navigation={true}
                modules={[Navigation]}
                className="mySwiper">
                    <SwiperSlide><ByDate data={data} setData={setData} rerenderStatus={rerenderStatus} /></SwiperSlide>
                    <SwiperSlide><a>Week</a></SwiperSlide>
                    <SwiperSlide><a>Month</a></SwiperSlide>
                </Swiper>
                </div>
                
                
            </div>
        
            
        </div>
    </div>
    )
}

export default Cal;
