    const [selectmonthMonth, setselectmonthMonth] = useState(currentDate.getMonth()+1);
    const changemonth_month = (e) => {
        setselectmonthMonth(e.target.value);
    };

    const [selectmonthYear, setselectmonthYear] = useState(currentDate.getFullYear());
    const changemonth_year = (e) => {
        setselectmonthYear(e.target.value);
    }

    const nextMonth = () => {

    }

    return (
        <div className="monthcontainer">
            <div className="caltop">
                <input className="changemonth_year" type="text" value={selectmonthYear} onChange={changemonth_year} />
                <input className="changemonth_month" type="text" value={selectmonthMonth} onChange={changemonth_month} />
            </div>
        </div>
    )
};

export default ByMonth;
