import Popup from "reactjs-popup";
import { HiOutlineMenu } from "react-icons/hi";
import { NavLink } from "react-router-dom";
import { AiOutlineDoubleLeft, AiOutlineDoubleRight } from "react-icons/ai";
import { useState } from "react";

const Cal = () => {

    const [selectdate, setselectdate] = useState("");
    const changedate = (e) => {
        setselectdate(e.target.value);
    }

    const adddate = () => {
        if (selectdate != "") {
            let dateobj = new Date(selectdate);
            dateobj.setDate(dateobj.getDate()+1);
            setselectdate(dateobj.getFullYear() + "-" + (dateobj.getMonth()+1) + "-" + dateobj.getDate());
        } else {
            return;
        }
    }

    return (
    <div>
        <div className="app">
            <div className="menu"><Popup trigger={<button className="menu"><HiOutlineMenu/></button>} modal nested>
                {
                    close => (
                        <div className='modal'>
                            <h3 className='content'>選擇頁面</h3>
                            <div class="navlink">
                                <NavLink to="/" style={{ textDecoration: 'none' }} ><li id="selectPage">主頁面</li></NavLink>
                                <NavLink to="/Calendar" style={{ textDecoration: 'none' }} ><li id="selectPage">日曆</li></NavLink>
                            </div>
                            <div>
                                <button id="closePopup" onClick=
                                    {() => close()}>
                                        Close
                                </button>
                            </div>
                        </div>
                    )
                }
                </Popup>
            </div>
            <div className="cal">
                <h1>CALENDAR</h1>
                <div className="selectDate">
                    <button className="changedate" id="lastdate"><AiOutlineDoubleLeft/></button>
                    <input className="changedate" type="date" value={selectdate} onChange={changedate} />
                    <button className="changedate" id="nextdate"><AiOutlineDoubleRight/></button>
                </div>
                <div>
                    <p>{(selectdate)}</p>
                </div>
            </div>
        
            
        </div>
    </div>
    )
}

export default Cal;
