import Menu from "../menu";
import { useEffect, useState } from "react";

const Importpage = ({ data, setData, settingprofile, setsettingprofile, rerenderStatus }) => {

    useEffect(()=> {
        if (localStorage.getItem("user") != null) {
            setData(JSON.parse(localStorage.getItem("user")))
        }
    }, ["a"])

    useEffect(()=> {
        if (rerenderStatus.current == false) {
            return;
        } else {
            localStorage.setItem('user', JSON.stringify(data));
            rerenderStatus.current = false; 
        }
    }, [data]);

    let showinputdatamenu = false;
    let showinputprofilemenu = false;

    const [InputData, setInputData] = useState();
    const DataInput = (e) => {
        let reader = new FileReader();
        reader.readAsText(e.target.files[0])
        reader.onload = (i) => {
            setInputData(i.target.result);
        }
        showinputprofilemenu = (InputData == "") ? false : true;
    }

    const [InputProfile, setInputProfile] = useState("");
    const ProfileInput = (e) => {
        setInputProfile(e.target.value);
        showinputprofilemenu = (InputProfile == "") ? false : true;
    }

    return (
        <div className="app">
            <div className="importpage">
                <h1>資料管理</h1>
                <div className="importcontainer">
                    <div>
                        <h3>匯入資料</h3>
                        <input type="file" accept=".json, .csv" onChange={DataInput} />
                        {showinputdatamenu}
                    </div>
                    <div>
                        <h3>匯入設定檔</h3>
                        <input type="file" accept=".json, .csv" />
                    </div>
                    <div>
                        <h3>匯出資料</h3>
                        <a
                            href={`data:text/json;charset=utf-8,${encodeURIComponent(
                            JSON.stringify(data)
                            )}`}
                            download="data.json"
                        >
                            {`Download Json`}
                        </a>
                    </div>
                    <div>
                        <h3>匯出設定檔</h3>
                        <a
                            href={`data:text/json;charset=utf-8,${encodeURIComponent(
                            JSON.stringify(settingprofile)
                            )}`}
                            download="profile.json"
                        >
                            {`Download Json`}
                        </a>
                    </div>
                </div>
            </div>
            <Menu />
        </div>
    )
}

export default Importpage;
