import { useState } from "react";
import { AiOutlineDoubleLeft, AiOutlineDoubleRight } from "react-icons/ai";

const ByWeek = () => {

    const currentDate = new Date();
    const [selectdayofaweek, setselectdate] = useState(currentDate.getFullYear() + "-" + (new String(currentDate.getMonth()+1)).padStart(2, "0") + "-" + (new String(currentDate.getDate())).padStart(2, "0") );

    return (
        <div className="weekcontainer">
            <div className="caltop">
                <button className="changeweek"><AiOutlineDoubleLeft/></button>
                <div className="changemonthcontainer">
                    <input className="changeweek_year" type="date"/>
                </div>
                <button className="changeweek"><AiOutlineDoubleRight/></button>
            </div>
        </div>
    )
};

export default ByWeek;
