import { useDebugValue, useEffect, useState } from "react";
import Menu from "../menu";

const GroupSetting = ({ data, setData, settingprofile, setsettingprofile, rerenderStatus }) => {

    useEffect(()=> {
        if (localStorage.getItem("user") != null) {
            setData(JSON.parse(localStorage.getItem("user")))
        }
    }, ["a"])

    useEffect(()=> {
        if (rerenderStatus.current == false) {
            return;
        } else {
            localStorage.setItem('user', JSON.stringify(data));
            rerenderStatus.current = false; 
        }
    }, [data]);

    let grouplist = data.map((item)=>   item.group    );
    const [selectedgroup, setselectedgroup] = useState("");
    let groupinsetting = settingprofile.group.map((d)=>d.name);
    const ChangeGroup = (e) => {
        setselectedgroup(e.target.value);
        if (!groupinsetting.includes(selectedgroup)) {
            setcolor(settingprofile.group.filter((item)=>item.name==selectedgroup)[0].color);
        }
    }

    const [color, setcolor] = useState("");
    const Changecolor = (e) => {
        setcolor(e.target.value);
    }

    useEffect(setcolor("#d62358"),[])

    return (
        <div className="app">
            <Menu/>
            <div className="groupsetting">
                <h1>Group Setting</h1>
                <h3 id="groupsetting">Color</h3>
                <div className="changecolor">
                    <input list="grouplist" id="changegroup" value={selectedgroup} onChange={ChangeGroup} />
                    <datalist id="grouplist">
                    {grouplist.map((d)=> {
                        return (
                            <option value={d} />
                        )
                    })}
                    </datalist>
                    <input type="color" id="inputcolor" value={color} onChange={Changecolor} />
                </div>
                {JSON.stringify(settingprofile.group.filter(item=>item.name==selectedgroup)[0].color)}
            </div>
        </div>
    )
};

export default GroupSetting;
