import { useState } from "react";

const ByMonth = () => {

    let currentDate = new Date();

    const [selectmonth_month, setselectmonth_month] = useState("");
    const changemonth_month = (e) => {
        setselectmonth_month(e.target.value);
    };

    const [selectmonth_year, setselectmonth_year] = useState("");
    const changemonth_year = (e) => {
        setselectmonth_year(e.target.value);
    }


    return (
        <div className="monthcontainer">
            <div className="caltop">
                <input className="changemonth_year" type="text" value={selectmonth_year} onChange={changemonth_year} />
                <input className="changemonth_month" type="text" value={selectmonth_month} onChange={changemonth_month} />
            </div>
        </div>
    )
};

export default ByMonth;
