import Popup from "reactjs-popup";
import { HiOutlineMenu } from "react-icons/hi";
import { NavLink } from "react-router-dom";
import { useEffect } from "react";
import ByDate from "./bydate";
import { Swiper, SwiperSlide } from "swiper/react"

const Cal = ({ data, setData, rerenderStatus }) => {

    useEffect(()=> {
        if (localStorage.getItem("user") != null) {
            setData(JSON.parse(localStorage.getItem("user")))
        }
    }, ["a"]);

    useEffect(()=> {
        if (rerenderStatus.current == false) {
            return;
        } else {
            localStorage.setItem('user', JSON.stringify(data));
            rerenderStatus.current = false; 
        }
    }, [data]);

    return (
    <div>
        <div className="app">
            <div className="menu"><Popup trigger={<button className="menu"><HiOutlineMenu/></button>} modal nested>
                {
                    close => (
                        <div className='modal'>
                            <h3 className='content'>選擇頁面</h3>
                            <div class="navlink">
                                <NavLink to="/" style={{ textDecoration: 'none' }} ><li id="selectPage">主頁面</li></NavLink>
                                <NavLink to="/Calendar" style={{ textDecoration: 'none' }} ><li id="selectPage">日曆</li></NavLink>
                            </div>
                            <div>
                                <button id="closePopup" onClick=
                                    {() => close()}>
                                        Close
                                </button>
                            </div>
                        </div>
                    )
                }
                </Popup>
            </div>
            <div className="cal">
                <h1>CALENDAR</h1>
                <Swiper>
                    <SwiperSlide>day</SwiperSlide>
                    <SwiperSlide>week</SwiperSlide>
                </Swiper>
                <select id="changecalendarviewtype">
                    <option>Day</option>
                    <option>Week</option>
                    <option>Month</option>
                </select>
                <ByDate data={data} setData={setData} rerenderStatus={rerenderStatus} />
            </div>
        
            
        </div>
    </div>
    )
}

export default Cal;
